"use strict"

import React, { Component } from 'react';
import { Button, Image, StyleSheet, TouchableOpacity, Text, View } from 'react-native';
import GlobalData from './js/components/GlobalData';

export default class App extends Component {
  state = { index: 0, showGlobalData: false }

  render() {
    let index = this.state.index;
    let nextIndex = index + 1;
    if (nextIndex === 3)
      index = 0

    return (
      <View style={styles.container}>
        <Button
          title="No images to upload"
          style={styles.dataButton}
          onPress={() => this.setState({ index: 1 })} />

        <View style={styles.dataButton}>
          <Button
            title="Daten"
          />
        </View>

        <View style={styles.camera}>
          <Button
            title="Hochladen"
          />
        </View>

        <GlobalData
          visible={this.state.showGlobalData} />


      </View>
    );
  }
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: 'lightgreen',
    alignItems: 'center',
    justifyContent: 'center'
  },
  camera: {
    height: 40,
    width: 40,
    position: 'absolute',
    bottom: -5,
    alignContent: 'center'
  },

  dataButton: {
    position: 'absolute',
    height: 60,
    width: 60,
    right: 15
  },
  folder: {
    width: 150,
    height: 150,
    bottom: 200,
    alignItems: 'center',
    justifyContent: 'center',
  },
  home: {
    height: 40,
    width: 40,
    position: 'absolute',
    bottom: 10,
    left: 20
  },
});
