import React, { Component } from 'react';
import { Button, Image, StyleSheet, Text, View } from 'react-native';
export default class App extends Component {

  state = { index: 0 }
  render() {
    let index = this.state.index;
    let nextIndex = index + 1;
    if (nextIndex === 3)
      index = 0

    return (
      <View style={styles.container}>
        <Button
          title="No images to upload"
          style={styles.text}
          onPress={() => this.setState({ index: 1 })} />

        <Image
          style={styles.folder}
          source={require('./assets/folder.png')} />
      </View>

    );
  }
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: 'lightgreen',
    alignItems: 'center',
    justifyContent: 'center',
  },
  folder: {
    width: 150,
    height: 150,
    bottom: 200,
    alignItems: 'center',
    justifyContent: 'center',
  },
  text: {
    bottom: 100
  },
  colors: {
    positiveButton: '#98B866',
    positiveText: '#3f6331',
    neutralButton: '#1976D2',
    utilButton: '#df8041',
    utilText: '#814115',
    iconGrey: 'rgb(96,96,96)',
    iconGreyDisabled: 'rgb(203,203,203)',
    whiteText: 'rgba(255,255,255,0.8)',
    errorBackground: 'rgb(241,55,62)',
    errorText: '#ffd3de',
    authBackground: '#3498db',
    modalBackground: 'rgba(0,0,0,0.6)'
  }

});
