import React, { Component } from 'react';
import { Button, Image, StyleSheet, Text, View } from 'react-native';
export default class App extends Component {

  state = { index: 0 }
  render() {
    let index = this.state.index;
    let nextIndex = index + 1;
    if (nextIndex === 3)
      index = 0

    return (
      <View style={styles.container}>
        <Button
          title="No images to upload"
          style={styles.text}
          onPress={() => this.setState({ index: 1 })} />

        <Image
          style={styles.folder}
          source={require('./assets/folder.png')} />
      </View>

    );
  }
}

const styles = StyleSheet.create({
  bottomRightIcon: {
    backgroundColor: 'transparent',
    position: 'absolute',
    right: 30,
    bottom: 0,
    zIndex: 1,
    paddingRight: 20,
    paddingTop: 20
  },
  bottomRow: {
    backgroundColor: 'transparent',
    marginBottom: 15,
    flex: 1,
    flexDirection: 'row',
    position: 'relative'
  },
  cameraButton: {
    backgroundColor: 'transparent',
    position: 'absolute',
    bottom: 0,
    left: SCREEN_WIDTH * 0.35,
    right: SCREEN_WIDTH * 0.35,
    justifyContent: 'center',
    alignItems: 'center',
    zIndex: 1
  },
  container: {
    flex: 1
  },
  paragraph: {
    margin: 24,
    fontSize: 18,
    textAlign: 'center'
  },
  noPermissionView: {
    flex: 1,
    alignItems: 'center',
    justifyContent: 'center',
    textAlign: 'center',
    padding: '12rem',
    backgroundColor: 'black'
  },
  noPermissionText: {
    color: 'white',
    textAlign: 'center',
    fontSize: '18rem'
  }
});
