import React, { PureComponent, useEffect, useState } from 'react';
import {
    Animated,
    ActivityIndicator,
    AppRegistry,
    TouchableOpacity,
    Alert,
    Dimensions,
    Platform,
    SafeAreaView,
    StyleSheet,
    Text,
    Linking,
    View
} from 'react-native';
import { Camera as ExpoCamera } from 'expo-camera';
import * as Location from 'expo-location';
import axios from 'axios';

const Camera = ({ navigation }) => {
    const [type, setType] = useState(ExpoCamera.Constants.Type.back);
    const [hasPermission, setHasPermission] = useState(null);
    const [location, setLocation] = useState(null);
    const [photoId, setPhotoId] = useState(null)
    const ref = React.useRef();

    useEffect(() => {
        (async () => {
            const { status } = await ExpoCamera.requestCameraPermissionsAsync();
            setHasPermission(status === 'granted');
        })();
    }, []);

    useEffect(() => {
        (async () => {
            let { status } = await Location.requestForegroundPermissionsAsync();
            if (status !== 'granted') {
                setErrorMsg('Permission to access location was denied');
                return;
            }

            let location = await Location.getCurrentPositionAsync({});
            setLocation(location);
        })();
    }, []);

    if (hasPermission === false) {
        return <Text>No access to camera</Text>;
    }

    const capture = async () => {
        if (ref) {
            let photo = await ref.current.takePictureAsync();
            console.log(photo)

            let formData = new FormData();
            let imgBlob = await (await fetch(photo.uri)).blob()
            //  console.log("JJJ")
            // console.log(imgBlob)

            formData.append(
                "files", imgBlob, Math.random())

            const data = new FormData();

            data.append('files', {
                uri: photo.uri,
                name: "test.jpg",
                type: 'multipart/form-data'
            });

            //  console.log(formData)
            const requestConfig = {
                method: 'POST',
                body: data,
            };

            const response = await fetch("http://178.18.252.126:1337/upload", requestConfig);

            let responseJson = await response.json();
            console.log(responseJson)
            setPhotoId(responseJson)


        }
    }

    const reportLitter = () => {
        const coords = location.coords;

        console.log({
            coordinates: coords.latitude + "," + coords.longitude,
            type: "plastic",
            sobadtosee: JSON.stringify(photoId)
        })

        axios.post("http://178.18.252.126:1337/litters", {
            coordinates: coords.latitude + "," + coords.longitude,
            type: "plastic",
            sobadtosee: photoId
        })
    }

    return <View style={styles.container}>
        <ExpoCamera style={styles.bottomRow} type={type} ref={ref}>
            <View style={styles.buttonContainer}>
                <TouchableOpacity
                    style={styles.button}
                    onPress={() => {
                        setType(
                            type === ExpoCamera.Constants.Type.back
                                ? ExpoCamera.Constants.Type.front
                                : ExpoCamera.Constants.Type.back
                        );
                    }}>
                    <Text style={styles.text}> Flip </Text>
                </TouchableOpacity>
                <TouchableOpacity
                    style={styles.button}
                    onPress={() => {
                        capture()
                    }}>
                    <Text style={styles.text}> Take Photo </Text>
                </TouchableOpacity>

                <TouchableOpacity
                    style={styles.cameraButton}
                    onPress={() => {
                        reportLitter()
                    }}>
                    <Text style={styles.text}> Send </Text>
                </TouchableOpacity>
            </View>
        </ExpoCamera>
    </View>
}

const SCREEN_HEIGHT = Dimensions.get('window').height;
const SCREEN_WIDTH = Dimensions.get('window').width;

const styles = StyleSheet.create({
    container: {
        flex: 1,
    },
    cameraButton: {
        backgroundColor: 'transparent',
        position: 'absolute',
        bottom: 0,
        left: SCREEN_WIDTH * 0.35,
        right: SCREEN_WIDTH * 0.35,
        justifyContent: 'center',
        alignItems: 'center',
        zIndex: 1
    },
    buttonContainer: {
        flex: 1,
        backgroundColor: 'transparent',
        flexDirection: 'row',
        margin: 20,
    }, bottomRow: {
        backgroundColor: 'transparent',
        marginBottom: 15,
        flex: 1,
        flexDirection: 'row',
        position: 'relative'
    },
    button: {
        flex: 0.1,
        alignSelf: 'flex-end',
        alignItems: 'center',
        backgroundColor: 'red',
        width: 80
    },
    text: {
        fontSize: 18,
        color: 'white',
    },
});

export default Camera;
