import React, { useEffect, useState } from 'react';
import { TouchableOpacity, Dimensions, StyleSheet, Text, View } from 'react-native';
import { Camera as ExpoCamera } from 'expo-camera';
import * as Location from 'expo-location';
import axios from 'axios';
import { Icon } from 'react-native-elements';
import LoadingCamera from './LoadingCamera';

const Camera = () => {
    const [type, setType] = useState(ExpoCamera.Constants.Type.back);
    const [hasPermission, setHasPermission] = useState(false);
    const [location, setLocation] = useState(null);
    const [photoId, setPhotoId] = useState(null)
    const ref = React.useRef();

    // Loading camera
    useEffect(() => {
        (async () => {
            const { status } = await ExpoCamera.requestCameraPermissionsAsync();
            setHasPermission(status === 'granted');
        })();
    }, []);

    // Load actual position of user
    useEffect(() => {
        (async () => {
            let { status } = await Location.requestForegroundPermissionsAsync();
            if (status !== 'granted') {
                setErrorMsg('Permission to access location was denied');
                return;
            }
            let location = await Location.getCurrentPositionAsync({});
            setLocation(location);
        })();
    }, []);

    // When the permission is not given
    if (hasPermission === false) {
        return <Text>No access to camera</Text>;
    }

//
    if (location == null) {
        return (
            <View>
                <LoadingCamera />
            </View>)
    }

    // Taking a picture
    const capture = async () => {
        if (ref && hasPermission == true) {
            let photo = await ref.current.takePictureAsync();

            //Multipart request
            const data = new FormData();

            data.append('files', {
                uri: photo.uri,
                name: "test.jpg",
                type: 'multipart/form-data'
            });

            const requestConfig = {
                method: 'POST',
                body: data,
            };

            const response = await fetch("http://178.18.252.126:1337/upload", requestConfig);

            let responseJson = await response.json();
            setPhotoId(responseJson)
        }
    }

    // Report litter
    const reportLitter = () => {
        const coords = location.coords;

        console.log({
            coordinates: coords.latitude + "," + coords.longitude,
            type: "Litter",
            pictureOfLitter: JSON.stringify("photoId:" + photoId)
        })

        axios.post("http://178.18.252.126:1337/litters", {
            coordinates: coords.latitude + "," + coords.longitude,
            type: "Litter",
            pictureOfLitter: photoId
        })
    }

    return (
        <ExpoCamera
            type={type}
            ref={ref}
            style={styles.cameraScreen}
        >
            <View style={styles.bottomRow}>
                <TouchableOpacity
                    style={styles.flipButton}
                    onPress={() => {
                        setType(
                            type === ExpoCamera.Constants.Type.back
                                ? ExpoCamera.Constants.Type.front
                                : ExpoCamera.Constants.Type.back
                        );
                    }}>
                    <Icon
                        color="white"
                        name="flip"
                        size={SCREEN_HEIGHT * 0.1}
                    />
                </TouchableOpacity>

                <TouchableOpacity
                    style={styles.cameraButton}
                    onPress={() => {
                        capture()
                    }}>
                    <Icon
                        color="white"
                        name="adjust"
                        size={SCREEN_HEIGHT * 0.1}
                    />
                </TouchableOpacity>

                <TouchableOpacity
                    style={styles.sendButton}
                    onPress={() => {
                        reportLitter()
                    }}>
                    <Icon
                        color="white"
                        name="send"
                        size={SCREEN_HEIGHT * 0.1}
                    />
                </TouchableOpacity>
            </View>
        </ExpoCamera>
    )
}

const SCREEN_HEIGHT = Dimensions.get('window').height;
const SCREEN_WIDTH = Dimensions.get('window').width;

const styles = StyleSheet.create({
    cameraScreen: {
        height: SCREEN_HEIGHT,
        width: SCREEN_WIDTH,
        flex: 1
    },
    cameraButton: {
        backgroundColor: 'transparent',
        position: 'absolute',
        bottom: 0,
        left: SCREEN_WIDTH * 0.35,
        right: SCREEN_WIDTH * 0.35,
        justifyContent: 'center',
        alignItems: 'center',
        zIndex: 1
    },
    bottomRow: {
        backgroundColor: 'transparent',
        marginBottom: 15,
        flex: 1,
        flexDirection: 'row',
        position: 'relative'
    },
    flipButton: {
        backgroundColor: 'transparent',
        position: 'absolute',
        bottom: 0,
        flexDirection: "row",
        justifyContent: "flex-end",
    },
    sendButton: {
        backgroundColor: 'transparent',
        position: 'absolute',
        bottom: 0,
        position: 'absolute',
        right: 5,
    }
});

export default Camera;
